import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [credentials, setCredentials] = useState({ name: "", email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  const endpoint = isLogin ? "login" : "signin";
  const body = isLogin
    ? { email: credentials.email, password: credentials.password }
    : credentials;

  const response = await fetch(`http://localhost:5000/api/auth/${endpoint}`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(body),
  });

  const data = await response.json();
  if (response.ok) {
    if (isLogin) {
      localStorage.setItem("token", data); // data is token string
      navigate("/home");
    } else {
      localStorage.setItem("token", data); // store token after signup
      navigate("/home"); // redirect to home after signup
    }
  } else {
    // Handle error messages from backend
    alert(data.error || (data.errors && data.errors[0]?.msg) || "Invalid credentials");
  }
};

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="card shadow-lg p-4" style={{ width: "100%", maxWidth: "400px" }}>
        <div className="text-center mb-4">
          <h3 className="fw-bold text-primary">{isLogin ? "Login to iNotebook" : "Create Your Account"}</h3>
          <p className="text-muted small">{isLogin ? "Enter your credentials to continue" : "Fill in details to register"}</p>
        </div>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="mb-3">
              <label className="form-label">Full Name</label>
              <input type="text" className="form-control" name="name" value={credentials.name} onChange={handleChange} required />
            </div>
          )}
          <div className="mb-3">
            <label className="form-label">Email address</label>
            <input type="email" className="form-control" name="email" value={credentials.email} onChange={handleChange} required />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input type="password" className="form-control" name="password" value={credentials.password} onChange={handleChange} required />
          </div>
          <button type="submit" className="btn btn-primary w-100">
            {isLogin ? "Login" : "Sign Up"}
          </button>
        </form>
        <div className="text-center mt-3">
          <button
            className="btn btn-link text-decoration-none"
            onClick={() => setIsLogin(!isLogin)}
          >
            {isLogin ? "Don't have an account? Sign up" : "Already have an account? Login"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
