import React from "react";

const Noteitem = ({ note, deleteNote, editNote }) => {
  const handleDelete = () => {
    deleteNote(note._id);
  };

  const handleEdit = () => {
    const title = prompt("Edit Title", note.title);
    const description = prompt("Edit Description", note.description);
    const tag = prompt("Edit Tag", note.tag);
    if (title && description) {
      editNote(note._id, title, description, tag);
    }
  };

  return (
    <div className="col-md-4">
      <div className="card bg-dark text-white border-secondary shadow-sm h-100">
        <div className="card-body d-flex flex-column justify-content-between">
          <div>
            <h5 className="card-title fw-semibold">{note.title}</h5>
            <p className="card-text">{note.description}</p>
            {note.tag && (
              <span className="badge bg-info text-dark">{note.tag}</span>
            )}
          </div>
          <div className="mt-3 d-flex justify-content-between">
            <button
              className="btn btn-outline-danger btn-sm w-45"
              onClick={handleDelete}
            >
              üóë Delete
            </button>
            <button
              className="btn btn-outline-light btn-sm w-45"
              onClick={handleEdit}
            >
              ‚úèÔ∏è Edit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Noteitem;
