import React, { useEffect } from 'react';
import { Link, useLocation } from "react-router-dom";
import './Navbar.css'; // import the custom styles

const Navbar = () => {
  const location = useLocation();

  useEffect(() => {
    console.log("Current Path:", location.pathname);
  }, [location]);

  if (location.pathname === "/") {
    return null;
  }

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow-sm py-3 sticky-top custom-navbar">
      <div className="container-fluid">
        <Link className="navbar-brand fs-4 fw-bold" to="/home">iNotebook</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent"
          aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link className={`nav-link ${location.pathname === "/home" ? "active" : ""}`} to="/home">Home</Link>
            </li>
            <li className="nav-item">
              <Link className={`nav-link ${location.pathname === "/about" ? "active" : ""}`} to="/about">About</Link>
            </li>
          </ul>
          <form className="d-flex" role="search">
            <input className="form-control rounded-pill custom-search" type="search" placeholder="Search notes..." aria-label="Search" />
          </form>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
